import React from "react";
import BlogCommentItem from "./BlogCommentItem";

const BlogComments = () => {
  return (
    <div className="comment-wrap">
      <h2 className="title">
        281 <span>Comments</span>
      </h2>

      <div className="latest-comments">
        <ul className="list-wrap">
          <li>
            <BlogCommentItem
              item={{
                src: "/img/blog/comment_avatar01.png",
                author: "Rahul Sharma",
                created_at: "March 17, 2023",
                comment: `यह केवल प्रिंटिंग और टाइपसेटिंग उद्योग का सरल डमी पाठ है। 1500 के दशक में, जब एक अज्ञात`,
              }}
            />
          </li>

          <li>
            <BlogCommentItem
              item={{
                src: "/img/blog/comment_avatar02.png",
                author: "Priya Singh",
                created_at: "March 17, 2023",
                comment: `यह केवल प्रिंटिंग और टाइपसेटिंग उद्योग का सरल डमी पाठ है। 1500 के दशक में, जब एक अज्ञात`,
              }}
            />

            <BlogCommentItem
              className={"children"}
              item={{
                src: "/img/blog/comment_avatar03.png",
                author: "Amit Patel",
                created_at: "March 17, 2023",
                comment: `यह केवल प्रिंटिंग और टाइपसेटिंग उद्योग का सरल डमी पाठ है। 1500 के दशक में, जब एक अज्ञात`,
              }}
            />
          </li>
        </ul>
      </div>
    </div>
  );
};

export default BlogComments;
