import React from "react";
import BlogItem from "./BlogItem";
import BlogPagination from "./BlogPagination";
import BlogSidebar from "./BlogSidebar/BlogSidebar";

const InnerBlogArea = () => {
  const blog_items = [
    {
      url: "/blog-details",
      src: "/img/blog/inner_blog_img01.jpg",
      tag: "Web3",
      reading_time: "5 Min",
      created_at: "March 17, 2022",
      author: "Aadi",
      title: "Unlocking the Power of Web3: A Beginner's Guide",
      desc: `Discover the fundamentals of Web3 and its transformative potential in decentralization, cryptocurrency, and beyond.`,
    },
    {
      url: "/blog-details",
      src: "/img/blog/inner_blog_img02.jpg",
      tag: "Blockchain",
      reading_time: "5 Min",
      created_at: "March 17, 2022",
      author: "Aadi",
      title: "Demystifying Blockchain: Understanding the Technology Behind Cryptocurrencies",
      desc: `Dive deep into the world of blockchain technology, exploring its architecture, consensus mechanisms, and real-world applications.`,
    },
    {
      url: "/blog-details",
      src: "/img/blog/inner_blog_img03.jpg",
      tag: "Animation",
      reading_time: "5 Min",
      created_at: "March 17, 2022",
      author: "Aadi",
      title: "The Art of Animation: Techniques and Trends in Digital Design",
      desc: `Explore the latest trends and techniques in digital animation, from 2D motion graphics to immersive 3D experiences, and unleash your creativity.`,
    },
  ];

  return (
    <section className="inner-blog-area pb-120">
      <div className="container">
        <div className="row justify-content-center">
          <div className="col-70">
            {blog_items.map((x, index) => (
              <BlogItem key={index} item={x} />
            ))}

            {/* pagination */}
            <BlogPagination />
          </div>

          <div className="col-30">
            <BlogSidebar />
          </div>
        </div>
      </div>
    </section>
  );
};

export default InnerBlogArea;
