import React from "react";
import SlickSlider from "../SlickSlider/SlickSlider";
import TestimonialAreaItem from "./TestimonialAreaItem";
import $ from "jquery";
import { doAnimations } from "../../lib/helpers";
import cn from "classnames";

const TestimonialArea = () => {
  const slick_settings = {
    dots: true,
    infinite: false,
    speed: 1000,
    autoplay: true,
    arrows: false,
    slidesToShow: 4,
    slidesToScroll: 1,
    beforeChange: function (currentSlide, nextSlide) {
      var $animatingElements = $(
        '.single-slider[data-slick-index="' + nextSlide + '"]'
      ).find("[data-animation]");
      doAnimations($animatingElements);
    },
    responsive: [
      {
        breakpoint: 1200,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 1,
          infinite: true,
        },
      },
      {
        breakpoint: 992,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
        },
      },
      {
        breakpoint: 767,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          arrows: false,
        },
      },
      {
        breakpoint: 575,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          arrows: false,
        },
      },
    ],
  };

  const testimonials = [
    {
      name: "Rajesh Gupta",
      designation: "Founder, TechGenius",
      comment: `TechGenius has helped us transform our business with their innovative solutions. Their team is highly skilled and dedicated.`,
      rating: 5,
    },
    {
      name: "Priya Patel",
      designation: "Marketing Manager, DigitalXcel",
      comment: `DigitalXcel's services have significantly boosted our online presence. Their marketing strategies are top-notch.`,
      rating: 4,
    },
    {
      name: "Suresh Sharma",
      designation: "Creative Director, PixelCraft Studios",
      comment: `PixelCraft Studios has exceeded our expectations with their creativity and attention to detail. Highly recommended.`,
      rating: 5,
    },
    {
      name: "Meera Singh",
      designation: "Senior Developer, CodeForge Solutions",
      comment: `CodeForge Solutions is a reliable partner for all our software needs. Their expertise has been invaluable to our projects.`,
      rating: 4,
    },
    {
      name: "Neha Gupta",
      designation: "HR Manager, TalentSpot Solutions",
      comment: `TalentSpot Solutions has helped us build a talented workforce. Their recruitment services are efficient and effective.`,
      rating: 5,
    },
  ];

  return (
    <section className="testimonial-area pt-110 pb-120">
      <div className="container">
        <div className="row justify-content-center">
          <div className="col-xl-6">
            <div className="section-title text-center mb-60">
              <span className="sub-title">Testimonials</span>
              <h2 className="title">What Our Customers Say</h2>
            </div>
          </div>
        </div>

        <div className="testimonial-item-wrap">
          <div className="row testimonial-active">
            <SlickSlider settings={slick_settings}>
              {testimonials.map((testimonial, index) => (
                <div
                  key={index}
                  className={cn(
                    "col-lg-3",
                    index % 2 === 0 && "testimonial-item-wrap-item-even"
                  )}
                >
                  <TestimonialAreaItem item={testimonial} />
                </div>
              ))}
            </SlickSlider>
          </div>
        </div>
      </div>
    </section>
  );
};

export default TestimonialArea;
