import React from "react";
import BlogAreaItem from "./BlogAreaItem";

const BlogArea = () => {
  const blog_area_items = [
    {
      src: "/img/blog/blog_img01.jpg",
      tag: "Web3 Development",
      title: "Demystifying Web3: A Comprehensive Guide to Decentralized Applications (DApps)",
      author_img: "/img/blog/blog_avatar01.png",
      author_name: "Isha Singh",
    },
    {
      src: "/img/blog/blog_img02.jpg",
      tag: "Animation Techniques",
      title: "Exploring Animation Techniques: From Keyframes to Motion Paths",
      author_img: "/img/blog/blog_avatar02.png",
      author_name: "Aarav Sharma",
    },
    {
      src: "/img/blog/blog_img03.jpg",
      tag: "Blockchain Technology",
      title: "Understanding Blockchain: Principles, Use Cases, and Future Implications",
      author_img: "/img/blog/blog_avatar03.png",
      author_name: "Swati ",
    },
  ];

  return (
    <section className="blog-area">
      <div className="container">
        <div className="row justify-content-center">
          <div className="col-lg-6">
            <div className="section-title text-center mb-50">
              <span className="sub-title">My Blog</span>
              <h2 className="title">News & Updates</h2>
              <p>
                Stay informed with comprehensive insights and analyses on web3 development, animation techniques, and blockchain technology.
              </p>
            </div>
          </div>
        </div>

        <div className="row justify-content-center">
          {blog_area_items.map((x, index) => (
            <div key={index} className="col-lg-4 col-md-6">
              <BlogAreaItem item={x} />
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default BlogArea;
